package controlstatement;

public class IfElseLadder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
				int a=10;
				int b=20;
				int c=1000;
				
				if(a>b&&a>c)
				{
					System.out.println("A is Greater number than  B and C");
				}
				
				else if(b>a&&b>c)
				{
					System.out.println("B is greater number than A and C");
				}
				
				else if(c>a&&c>b)
				{
					System.out.println("C is greater than A and B");
				}
										}

						}
