package controlstatement;

public class OddEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			
		int i=140;
		
		if(i%2==0)
		{
			System.out.println("Number is Even Number");
		}
		else
		{
			System.out.println("Number is Odd");
		}
	}

}
