package operators;

public class IncOperatorforOddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
			int i=10;
			
			for(i=0;i<=10;i++)
			{
				if(i%2==0)
				{
					System.out.println("Its a Even Number"+i);
				}
				else
				{
					System.out.println("Its a Odd number"+i);
				}
			}
	}

}
