package variables;

public class VariablesExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=100;
		System.out.println("Value of Int Data Type is "+num1);
		
		float fl1=121232.1234F;
		System.out.println("Value of Float Data Type is "+fl1);
		
		double db1=12.123344664545441;
		System.out.println("Value of Double  Data Type is "+db1);
				
		char ch1 ='A';
		System.out.println("Value of Char Data Type is "+ch1);
		
		String str1="Ankur";
		System.out.println("Value of String Data Type is "+str1);
		
		boolean Bl1=true;
		System.out.println("Value of Boolean Data Type is "+Bl1);
		
		byte bt1=123;
		System.out.println("Value of Byte Data Type is "+bt1);
		
		long ln=1234567891011111443l;
		System.out.println("Value of Long  Data Type is "+ln);
		
			
	}

}
